export default {


	getYoutubeTrailerLink:  () => {

		var res = movie_videos.data
		var arr =  res.results
		//for now only getting and check the first item 
		// todo iterate in all array elements for check
		if (arr.length > 0 && arr[0].site == "YouTube"){
			return "https://www.youtube.com/watch?v=" + arr[0].key
		}
		return undefined
	}
}
