async () => {
  var res = await movie_videos.run({
    "movie_id": movies_popular.selectedItem.id
  });
  var arr = res.results;
  if (arr.length > 0 && arr[0].site == "YouTube") {
    await storeValue(movies_details_helper.MOVIE_ITEM_YT_LINK, "https://www.youtube.com/watch?v=" + arr[0].key);
  }
  var kres = await movie_keywords.run({
    "movie_id": movies_popular.selectedItem.id
  });
  await storeValue(movies_details_helper.MOVIE_ITEM_KEYWORDS, kres.keywords);
  showModal("movie_item_details");
  return undefined;
}

